precision mediump float;

uniform float time;

const float pi = 3.14159;

void main () {
    vec2 uv = (gl_FragCoord.xy - 0.5) / 7.0;
    uv.y = 1.0 - uv.y;

    float wave1 = abs(sin(clamp(uv.y, 0.0, 0.34) * pi * 3.0)) * abs(sin((uv.x - time / -2.0) * pi * 2.0)) * abs(cos(time * pi));
    float wave2 = abs(sin(clamp(uv.y, 0.67, 1.0) * pi * 3.0)) * abs(sin((uv.x - time / 2.0) * pi * 2.0)) * abs(sin(time * pi));
    float wave = floor((wave1 + wave2) * 9.0) / 8.0;

    gl_FragColor = vec4(vec3(mix(0.64, 1.0, clamp(wave, 0.0, 1.0))), 0.71);
}
